const regexUser = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

export default function validation(userData){
    let errors = {}

    if(!regexUser.test(userData.username)){
        errors.username = "El usuario debe ser un email"
    }else if(!userData.username){
        
    } 
    else if(userData.username.length > 35) errors.username = "El nombre de usuario no puede tener mas de 35 caracteres"
    else if(!userData.password.match(/\d/))
    errors.password = "La contraseña debe tener al menos un número"
    else if(userData.password.length < 6 && userData.password.length > 10 ) errors.password = "La contraseña debe contener entre 6 y 10 caracteres"

    return errors;
}
