import styles from "./Card.module.css";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { useState } from "react";
import { addFavorite, deleteFavorite } from "../../redux/actions";

export default function Card(props) {
   const dispatch = useDispatch()
   const favorites = useSelector((state)=>state.myFavorites)
   const [isFav, setIsFav] = useState(false);

   const handleFavorite = () => {
      if(isFav){
         setIsFav(false);
         dispatch(deleteFavorite(props.detailId))
      }else{
         setIsFav(true)
         dispatch(addFavorite(props.name, props.gender, props.onClose, props.species, props.image, props.id))
      }
   }

   return (
      <div className={styles.contenedor}>
         {
            isFav ? (
               <button onClick={handleFavorite}>‚ù§Ô∏è</button>
            ) : (
               <button onClick={handleFavorite}>ü§ç</button>
            )
         }
         <button onClick={props.onClose} className={styles.boton}>X</button>
         <Link to={`/detail/${props.detailId}`}>
            <h2>Nombre: {props.name}</h2>
         </Link>
         <img  src={props.image} alt="imagen principal" />
         <h2>Specie: {props.species}</h2>
         <h2>Gender: {props.gender}</h2>
      </div>
   );
}
