    return(
        <>
        {
            character ? (
                <div>
                    <div>
                        <h1>{character.name}</h1>
                        <h3>{character.status}</h3>
                        <h3>{character.species}</h3>
                        <h3>{character.gender}</h3>
                        <h3>{character.origin?.name}</h3>
                    </div>
                    <div>
                        <img src={character.image} alt={character.name} />
                    </div>
                </div>
            ) : ("No existe el personaje")
        }
        </>
    )
}
