import { ADD_FAVORITE } from "./action-types";

const initialState = {
    myFavorites: [],
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case ADD_FAVORITE:
            return{
                ...state,
                myFavorites: [...state.myFavorites]
            }
        default:
            return {...state}
    }
};

export default reducer;
